mapper.select:
select id,customer_id,children_id,customer_device_number,children_device_number from customer_child_device where  is_deleted = 1

	 and id =#{id}


	 and customer_id =#{customerId}

select c1.*,c2.customer_id,c2.children_id from customer_base c1
left join customer_child_device c2 on c1.id = c2.customer_id and c2.is_deleted = 1
where c1.is_deleted = 1 

	 and id =#{id}


	 and customer_id =#{customerId}


	 and children_id =#{childrenId}


	 and customer_device_number =#{customerDeviceNumber}


	 and children_device_number =#{childrenDeviceNumber}

order by c2.customer_id asc,c2.children_id asc

select count(c1.id) from customer_base c1
left join customer_child_device c2 on c1.id = c2.customer_id and c2.is_deleted = 1
where c1.is_deleted = 1 

	 and id =#{id}


	 and customer_id =#{customerId}


	 and children_id =#{childrenId}


	 and customer_device_number =#{customerDeviceNumber}


	 and children_device_number =#{childrenDeviceNumber}

mapper.update:
update customer_child_device set id=#{id}

	,customer_id =#{customerId}


	 ,children_id =#{childrenId}


	 ,customer_device_number =#{customerDeviceNumber}


	 ,children_device_number =#{childrenDeviceNumber}


    ,updater=#{updater}


    ,update_time=#{updateTime}

where id=#{id} and is_deleted=1
